import UIKit

enum Catalog {
    
    enum Loader {
        struct Response {
            let isLoaderVisible: Bool
        }
        struct ViewModel {
            let isLoaderVisible: Bool
        }
    }
    
    enum Banners {
        struct Request {
            let banners: [CatalogModel.ViewModel.BannerModel]
        }
        struct WorkerRequest {
            let url: String
            let index: Int
            let completion: (Int, Result<UIImage, Error>) -> Void
        }
        struct Response {
            let image: UIImage
            let index: Int
        }
        struct ViewModel {
            let image: UIImage
            let index: Int
        }
    }
    
    enum Favorite {
        struct Request {
            let row: Int
        }
        struct Response {
            let isFavorite: Bool
            let row: Int
        }
        struct ViewModel {
            let favoriteImage: UIImage?
            let row: Int
        }
    }
    
    enum TypeModel {
        struct Request {
            let type: CatalogType
        }
    }
    
    enum CatalogModel {
        struct Request {
            let type: CatalogType
            let completion: (Result<ServerResponse<CatalogReponse>, Error>) -> Void
        }
        struct Response {
            let catalog: CatalogReponse
        }
        struct ViewModel {
            struct DealModel {
                let isImageCuttedVisible: Bool
                let imageUrl: String
                let partnerName: String
                let title: String
                let priceDescription: String
                let priceSymbol: String
